import React, { useState, useRef } from 'react';

function App() {
  const [price, setPrice] = useState('');
  const canvasRef = useRef(null);

  const generateImage = () => {
    if (!price) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();
    img.src = '/img5.png'; // in public folder

    img.onload = () => {
      canvas.width = img.width;
      canvas.height = img.height;
      ctx.drawImage(img, 0, 0);

      // Draw date
      const today = new Date();
      const dateStr = today.toLocaleDateString('en-GB'); // dd/mm/yyyy
      ctx.font = '40px Arial';
      ctx.fillStyle = 'black';
      ctx.fillText(dateStr, 70, 165);

      // Draw price
      ctx.font = '120px Arial';
      ctx.fillText(price + '/-', 380, 800);
    };
  };

  const downloadImage = () => {
    const canvas = canvasRef.current;
    canvas.toBlob((blob) => {
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'updated_image.png';
      a.click();
      URL.revokeObjectURL(url);
    });
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Mandi Rate Image Generator</h1>
      <input
        type="text"
        placeholder="Enter the rate"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
      />
      <br />
      <button onClick={generateImage}>Generate Image</button>
      <br />
      <canvas ref={canvasRef} style={{ border: '1px solid black', marginTop: '20px' }}></canvas>
      <br />
      <button onClick={downloadImage}>Download Image</button>
    </div>
  );
}

export default App;
